import React, { useEffect, useState } from "react";

function Cards() {
  const [loading, setLoading] = useState(true);
  const [picsArrived, setPicsArrived] = useState(false);
  let [ExampleArr, setExampleArr] = useState([
    "heloo",
    "meh",
    "lame",
    "brother",
    "layman",
    "fool",
    "lanbs",
    "gerrw",
    "dewew",
  ]);

  let ArrTotal = [];

  useEffect(() => {
    fetch("https://api.pexels.com/v1/search/?page=1&per_page=80&query=flower", {
      headers: {
        Authorization:
          "563492ad6f917000010000010bbc2cb4ac214d6d8ecd66c450d9526c",
      },
    })
      .then((resp) => {
        if (!resp.ok) {
          throw Error("Could not fetch data for that resource");
        }
        return resp.json();
      })
      .then((data) => {
        console.log(data);
        ArrTotal = data.photos;
        console.log(ArrTotal);
        setPicsArrived(true);
        setLoading(false);
        console.log("use effect used");
      })
      .catch((e) => {
        console.log(e.message);
        setLoading(false);
      });
  }, []);

  let checkArr = [];
  let arrToReplace = [];

  useEffect(() => {
    checkArr = [];
    arrToReplace = [];
    console.log("USE EFFECT");
  }, [ExampleArr]);

  function CheckUnique(index) {
    if (checkArr.indexOf(index) === -1) {
      checkArr.push(index);
      return ExampleArr[index];
    } else {
      let index = Math.floor(ExampleArr.length * Math.random());
      let elseVal = CheckUnique(index);
      return elseVal;
    }
  }

  function changeOrder() {
    for (let i = 0; i < ExampleArr.length; i++) {
      let index = Math.floor(ExampleArr.length * Math.random());
      console.log(index);
      if (checkArr.length > ExampleArr.length) {
        break;
      }
      let uniqueArr = CheckUnique(index);
      arrToReplace.push(uniqueArr);
    }
    ExampleArr = arrToReplace;
    console.log(arrToReplace);
    setExampleArr(ExampleArr);
  }
  return (
    <div className="container">
      {loading && <h1 className="loadingHead">Loading...</h1>}
      {picsArrived ? (
        <div className="CardsRendered">
          {ExampleArr.map((e) => {
            return <h1 onClick={changeOrder}>{e}</h1>;
          })}
        </div>
      ) : (
        <div>
          <h1 className="loadingHead">Error...</h1>
        </div>
      )}
    </div>
  );
}

export default Cards;
